import { Injectable } from '@angular/core';
import { Order } from '../models/order.model';
import { API_URL } from 'src/environment/environment';
import { HttpClient } from '@angular/common/http';

@Injectable({
  providedIn: 'root'
})
export class OrderService {
  user_id: number;
  user_orders: Order[] = [];

  constructor(
    private http: HttpClient
  ) {
    this.user_id = JSON.parse(localStorage.getItem('user') as string).id;
  }

  getOrders(): Order[] {

    this.http.get(API_URL + `/users/${this.user_id}/orders`).subscribe(
      (data: any) => {
        this.user_orders.push(new Order(
          data.id,
          data.price,
          data.status,
          data.orderDetails
        ));
      }
    )

    console.log(this.user_orders);

    return this.user_orders;
  }

}
