import { HttpClient } from '@angular/common/http';
import { ApiService } from './api.service';
import { Injectable } from '@angular/core';
import { API_URL } from 'src/environment/environment';

@Injectable({
  providedIn: 'root'
})
export class AuthService {

  constructor(
    private http: HttpClient,
    private apiService: ApiService
  ) { }

  signup(username: string, email: string, password: string) {
    return this.http.post(API_URL + '/users', { email, password }).subscribe((data) => {
      console.log(data);

      //this.apiService.setToken(data['token']);
    });

    // return this.http.post(API_URL + '/products').subscribe((data) => {
    //   this.sendAllProducts(data);
    // });
    // return true;
  }

  login(email: string, password: string) {
    return this.http.post(API_URL + '/users/login', { email, password }).subscribe((data) => {
      console.log(data);

    });
  }

  logout() {
    localStorage.removeItem('token');
  }

  loggedIn() {
    return !!localStorage.getItem('token');
  }

  setToken(token: string) {
    localStorage.setItem('token', token);
  }

  getToken() {
    return localStorage.getItem('token');
  }

}
