import { AuthService } from 'src/app/services/auth.service';
import { Injectable } from '@angular/core';
import { Order } from '../models/order.model';
import { API_URL } from 'src/environment/environment';
import { HttpClient, HttpHeaders, HttpClientModule } from '@angular/common/http';
import { BehaviorSubject, Observable, catchError, map, of } from 'rxjs';
import { Role } from '../models/role.model';
import { OrderDetail } from '../models/orderDetail.model';
import { User } from '../models/user.model';

@Injectable({
  providedIn: 'root'
})
export class OrderService {
  private userOrdersSubject: BehaviorSubject<any> = new BehaviorSubject<any>([]);
  public userOrders$: Observable<any> = this.userOrdersSubject.asObservable();
  user_id: number;
  user_token: string;
  user_orders: Order[] = [];
  productsSubject: any;

  constructor(
    private http: HttpClient,
    private authService: AuthService
  ) {
    this.user_id = JSON.parse(localStorage.getItem('user') as string).id;
    this.user_token = this.authService.getToken() as string;
  }

  setUserOrders(value: any): void {
    this.userOrdersSubject.next(value);
  }

  getOrders(): Observable<Order[]> {
    const headers = new HttpHeaders().set('Authorization', `Bearer ${this.user_token}`);
    return this.http.get(`${API_URL}/users/${this.user_id}`, { headers }).pipe(
      map((data: any) => {
        console.log(data);

        data.orders.forEach((order: any) => {
          this.user_orders.push(order);
        })

        this.setUserOrders(this.user_orders);

        return this.user_orders; // Vous pouvez ajuster cela en fonction de votre logique
      }),
      catchError((error: any) => {
        console.error('Erreur lors de la récupération des commandes :', error);
        return of([]); // Renvoie un tableau vide en cas d'erreur
      })
    );
  }

}
