import { HttpClient, HttpErrorResponse } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { API_URL } from 'src/environment/environment';
import { Router } from '@angular/router';
import { BehaviorSubject, Observable, catchError, tap, throwError } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class AuthService {
  private isLoggedInSubject: BehaviorSubject<boolean> = new BehaviorSubject<boolean>(false);
  public isLoggedIn$: Observable<boolean> = this.isLoggedInSubject.asObservable();

  constructor(
    private http: HttpClient,
    private router: Router
  ) { }

  setLoggedIn(value: boolean): void {
    this.isLoggedInSubject.next(value);
  }

  signup(username: string, email: string, password: string): Observable<any> {
    return this.http.post(API_URL + '/auth/signup', { username, email, password }).pipe(
      tap((data: any) => {
        this.login(data.username, data.password);
      }),
      catchError(this.handleError)
    )
  }

  login(username: string, password: string): any {
    console.log("login", username, password);
    return this.http.post(`${API_URL}/auth/signin`, { username, password }).subscribe(
      (data: any) => {
        console.log(data);
        this.setToken(data.accessToken);
        this.setLoggedIn(true);
        this.router.navigate(['/']);
      }
    );
  }
  // login(username: string, password: string): Observable<any> {
  //   console.log("login", username, password);
  //   return this.http.post(`${API_URL}/auth/signin`, { username, password }).pipe(
  //     tap((data: any) => {
  //       console.log(data);
  //       this.setToken(data.accessToken);
  //       this.setLoggedIn(true);
  //     }),
  //     catchError(this.handleError)
  //   );
  // }

  // login(username: string, password: string): Observable<any> {
  //   return this.http.post(`${API_URL}/auth/signin`, { username, password });
  // }

  logout(): void {
    localStorage.removeItem('token');
    this.setLoggedIn(false);
  }

  loggedIn(): boolean {
    return !!localStorage.getItem('token');
  }

  setToken(token: string): void {
    localStorage.setItem('token', token);
    this.router.navigate(['/']);
  }

  getToken(): string | null {
    return localStorage.getItem('token');
  }

  private handleError(error: HttpErrorResponse): Observable<never> {
    if (error.status === 401) {
      // Gérer l'erreur 401 (non autorisé)
      console.error('Erreur 401 : Accès non autorisé');
      // Vous pouvez rediriger l'utilisateur vers la page de connexion ou effectuer d'autres actions ici
    } else {
      // Gérer d'autres erreurs
      console.error('Erreur non gérée :', error);
    }

    // Renvoie une observable avec un message d'erreur convivial
    return throwError(() => new Error('Une erreur s\'est produite. Veuillez réessayer plus tard.'));
  }

}
