import { AuthService } from 'src/app/services/auth.service';
import { Injectable } from '@angular/core';
import { Order } from '../models/order.model';
import { API_URL } from 'src/environment/environment';
import { HttpClient, HttpHeaders } from '@angular/common/http';

@Injectable({
  providedIn: 'root'
})
export class OrderService {
  user_id: number;
  user_token: string;
  user_orders: Order[] = [];

  constructor(
    private http: HttpClient,
    private authService: AuthService
  ) {
    this.user_id = JSON.parse(localStorage.getItem('user') as string).id;
    this.user_token = this.authService.getToken() as string;
  }

  getOrders(): Order[] {
    console.log(this.user_id);
    const headers = new HttpHeaders().set('Authorization', `Bearer ${this.user_token}`);
    this.http.get(API_URL + `/users/${this.user_id}`, { headers }).subscribe(
      (data: any) => {
        this.user_orders.push(new Order(
          data.id,
          data.price,
          data.status,
          data.orderDetails
        ));
      }
    )

    console.log(this.user_orders);

    return this.user_orders;
  }

}
